import React, { Component } from 'react'

export class Product extends Component {
    constructor(props){
        super(props)
        this.state = {
            current_size:this.props.item.sizes[1]
        }
        this.setSize = this.setSize.bind(this)
    }
  render() {
    return (
      <div className='product-block'>
        <div className='image'>
          <img src={'./images/'+this.props.item.img} height="350"/>
        </div>
        <h3>{this.props.item.name}</h3>
        <div>{this.props.item.sizes.map(size => 
         <div key={size.size} className={this.isActiveSize(size)} onClick={() => this.setSize(size)}>{size.name}</div> 
          )}</div>
          <b>{this.state.current_size.price}</b>
      </div>
    )
  }
  setSize(size){
    this.setState({current_size:size})
  }
  isActiveSize(size){
    if (size === this.state.current_size) {
      return 'size-tab active'
    } else {
      return 'size-tab'
    }
  }
}

export default Product
