import React, { Component } from 'react'

export class Product extends Component {
    constructor(props){
        super(props)
        this.state = {
            current_type:this.props.item.sizes[1]
        }
    }
  render() {
    return (
      <div className='product-block'>
        <h1>{this.props.item.name}</h1>
      </div>
    )
  }
}

export default Product
