import React, { Component } from 'react'
import {FaPizzaSlice, FaFontAwesome} from 'react-icons/fa'

export class Categories extends Component {
    constructor(props){
        super(props)
        this.state = {
            categories:[
                {
                    'key':'all',
                    'name':'Все'
                },
                {
                    'key':'fast-food',
                    'name':'Фаст фуд'
                },
                {
                    'key':'combo',
                    'name':'Комбо'
                },
                {
                    icon: <FaPizzaSlice className='fa-icon'/>,
                    'key':'sushi',
                    'name':'суши'
                },
                {
                    icon: <FaPizzaSlice className='fa-icon'/>,
                    'key':'pizza',
                    'name':'Пиццы'
                }
            ]
        }
    }
  render() {
    return (
      <div className='categories'>
        {this.state.categories.map(el => (
            <div key={el.key} onClick={() => this.props.choose(el.key)}>{el.icon}{el.name}</div>
        ))}
      </div>
    )
  }
}

export default Categories
