import ProductsList from './components/ProductsList';
import data from './data';
import React from 'react';
import Header from './components/Header';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      products:data,
      orders:[]
    }
    this.addToCart = this.addToCart.bind(this)
  }
  render(){
    return (
      <div className="App">
        <Header orders={this.state.orders}/>
        <ProductsList onAdd={this.addToCart} items={this.state.products}/>
      </div>
    );
  }
  addToCart(item){
    let isInCart = false
    this.state.orders.forEach(el => {
      if(item.id === el.id && item.size === el.size){
        isInCart = true
      }
    })
    if (isInCart) {
      const order = this.state.orders.find(el => el.id === item.id && el.size === item.size)
      order.count += 1
      this.setState({orders: this.state.orders.map(u => u.id !== item.id && u.size !==item.size ? u : order)})
    } else {
      item.count = 1
      this.setState({orders: [...this.state.orders, item]})  
    }  
    console.log(isInCart);   
  }
}

export default App;
