import React, { Component, useState} from 'react'
import { FaShoppingCart, FaTrash } from "react-icons/fa";

const showOrders = (props) => {
  return(
    <div>
      
    </div>
  )
}
const showNothing = () => {
  return(
    <h2>Пока нет товаров</h2>
  )
}

export default function Header(props){
    let [cartOpen, setCartOpen] = useState(false)
    // let count = 0
    // props.orders.forEach(el => count += el.count);
    return (
      <header>
        <div>
            <a className='Logo'>Доставка</a>
        </div>
        <div className='header-menu'>
            {/* <div className='cart-items'>{count}</div> */}
            <FaShoppingCart className={`header-menu-btn cart`}/>
            <a className='header-menu-btn'>Про нас</a>
            <a className='header-menu-btn'>Контакты</a>
            <a className='header-menu-btn'>Кабинет</a>
            {cartOpen && (
              <div className='shop-cart'>
                {props.orders.length > 0 
                  ?showOrders(props)
                  :showNothing()
                }
              </div>
            )}
        </div>
      </header>
    )
  }
