import ProductsList from './components/ProductsList';
import data from './data';
import React from 'react';
import Header from './components/Header';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      products:data,
      orders:[]
    }
    this.addToCart = this.addToCart.bind(this)
    this.clearCart = this.clearCart.bind(this)
    this.orderIncrement = this.orderIncrement.bind(this)
    this.orderDecrement = this.orderDecrement.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
  }
  render(){
    return (
      <div className="App">
        <Header onDelete={this.deleteOrder} orders={this.state.orders} onUp={this.orderIncrement} onDown={this.orderDecrement}/>
        <ProductsList onAdd={this.addToCart} items={this.state.products}/>
      </div>
    );
  }
  clearCart(){
    this.setState({orders: []})
  }
  deleteOrder(item){
    this.setState({orders: this.state.orders.filter(el => el !== item)})
  }
  addToCart(item){
    let isInCart = false
    this.state.orders.forEach(el => {
      if(item.id === el.id && item.size === el.size){
        isInCart = true
      }
    })
    if (isInCart) {
      const order = this.state.orders.find(el => el.id === item.id && el.size.size === item.size.size)
      order.count += 1
      console.log(this.state.orders);
      this.setState({orders: this.state.orders.map(u => u !== order ? u : order)})
    } else {
      item.count = 1
      this.setState({orders: [...this.state.orders, item]})
    }    
  }
  orderDecrement(item){
    if(item.count > 1){
      item.count -= 1
      this.setState({orders: this.state.orders.map(u => u !== item ? u : item)})
    }else{
      this.setState({orders: this.state.orders.filter(el => el !== item)})
    }
  }
  orderIncrement(item){
    item.count += 1
    this.setState({orders: this.state.orders.map(u => u !== item ? u : item)})
  }
}

export default App;
