import React, { Component } from 'react'

export class Product extends Component {
    constructor(props){
        super(props)
        this.state = {
            current_size:this.props.item.sizes[1],
        }
        this.state.item = {
          id:this.props.item.id,
          name:this.props.item.name+" "+this.state.current_size.name,
          img:this.props.item.img,
          category:this.props.item.category,
          price:this.state.current_size.price
        }
        this.show = this.show.bind(this)
        this.setSize = this.setSize.bind(this)
      }
      render() {
        return (
          <div className='product-block'>
            <div className='image'>
              <img src={'./images/'+this.props.item.img}/>
            </div>
            <h3>{this.props.item.name}-{this.state.current_size.name}</h3>
            <b>{this.state.current_size.price}сом</b>
            <div className='btns-group'>
              <div className='size-tabs'>{this.props.item.sizes.map(size => 
              <div key={size.size} className={this.isActiveSize(size)} onClick={() => this.setSize(size)}>{size.key}</div> 
                )}</div>
                <button className='add-to-cart-btn' onClick={() => this.show}>В Корзину</button>
            </div>
          </div>
        )
      }
      setSize(size){
        this.setState({current_size:size})
        this.state.item = {
          id:this.props.item.id,
          name:this.props.item.name+" "+this.state.current_size.name,
          img:this.props.item.img,
          category:this.props.item.category,
          price:this.state.current_size.price
        }
      }
      show(){
        console.log(this.state.item);
      }
      isActiveSize(size){
        if (size === this.state.current_size) {
          return 'size-tab active'
        } else {
          return 'size-tab'
        }
      }
    }


export default Product
