import React, { Component, useState } from 'react'
import { FaShoppingCart, FaTrash } from "react-icons/fa";
import Order from './Order';

const showOrders = (props) => {
  let summ = 0
  props.orders.forEach(el => summ += el.count*Number.parseFloat(el.price));
  return(
    <div>
      {props.orders.map(order => (
        <Order onDelete={props.onDelete} onUp={props.onUp} onDown={props.onDown} key={order.id} item={order}/>
      ))}
      <div className='cart-tools'>
        <div className='clear-cart' onClick={() => props.onClear()}>
          <FaTrash className='delete-icon'/> Очистить корзину
        </div>
        <p className='summ'>Сумма: ${Intl.NumberFormat().format(summ)}</p>
      </div>
    </div>
  )
}
const showNothing = () => {
  return(
    <h2>Пока нет товаров</h2>
  )
}

export default function Header(props){
    let [cartOpen, setCartOpen] = useState(false)
    let count = 0
    props.orders.forEach(el => count += el.count);
    return (
      <header>
        <div>
            <a className='Logo'>Shop React</a>
        </div>
        <div className='header-menu'>
            <div className='cart-items'>{count}</div>
            <FaShoppingCart onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`header-menu-btn cart ${cartOpen && 'active'}`}/>
            <a className='header-menu-btn'>Про нас</a>
            <a className='header-menu-btn'>Контакты</a>
            <a className='header-menu-btn'>Кабинет</a>
            {cartOpen && (
              <div className='shop-cart'>
                {props.orders.length > 0 
                  ?showOrders(props)
                  :showNothing()
                }
              </div>
            )}
        </div>
      </header>
    )
  }
